mapper.select:
        select a.quality_check_id as qualityCheckId,a.company_id as companyId,a.project_id as projectId,
		a.quality_check_name as qualityCheckName,a.finished_date as finishedDate,a.start_date as startDate,
		a.type,a.status,a.state,a.create_time as createTime,a.inform_users as informUsers,a.process as process,
		a.project_paper_id as projectPaperId,a.x,a.y,a.jaso_user_id as jasoUserId,a.nature_id as natureId,
		a.if_delete as if_delete, b.user_real_name as userRealName,c.paper_url as projectPaperUrl,d.nature_name as natureName from quality_check a 
		LEFT JOIN jaso_user b on a.jaso_user_id=b.jaso_user_id LEFT JOIN project_paper c on a.project_paper_id=c.project_paper_id left join nature d on a.nature_id=d.nature_id where a.if_delete=0 

           and a.company_id=#{companyId}


           and a.jaso_user_id=#{jasoUserId} 


           and a.project_id=#{projectId}


           and (a.status=2 or a.status=3)


           and a.nature_id=#{natureId}


           and DATE_FORMAT(a.finished_date, '%Y-%m-%d')>=  DATE_FORMAT(#{startTime}, '%Y-%m-%d')


           and DATE_FORMAT(a.finished_date, '%Y-%m-%d')<=  DATE_FORMAT(#{endTime}, '%Y-%m-%d')


           and a.type = #{type}


           and a.status = #{status}


            and (a.quality_check_name like #{searchContent} 
		    or a.jaso_user_id in (select jaso_user_id from jaso_user where user_real_name like #{searchContent}) 
		    or a.quality_check_id in 
		    (select a.about_id from quality_check_type a,project_check_type b 
		    where a.type=1 and a.project_check_type_id=b.project_check_type_id 
		    and b.check_name like #{searchContent}))

         ORDER BY a.quality_check_id DESC

select a.quality_check_id as qualityCheckId,a.company_id as companyId,a.project_id as projectId, a.quality_check_name as qualityCheckName,a.finished_date as finishedDate,a.start_date as startDate, a.type,a.status,a.state,a.create_time as createTime,a.inform_users as informUsers, a.project_paper_id as projectPaperId,a.x,a.y,a.jaso_user_id as jasoUserId,a.nature_id as natureId, a.if_delete as if_delete, b.user_real_name as userRealName,c.paper_url as projectPaperUrl,d.nature_name as natureName from quality_check a left join jaso_user b on a.jaso_user_id=b.jaso_user_id left join project_paper c on a.project_paper_id=c.project_paper_id left join nature d on a.nature_id=d.nature_id where a.if_delete=0
and a.company_id=#{companyId}
and a.project_id=#{projectId}
and a.quality_check_id=#{qualityCheckId}

select a.quality_check_users_id as qualityCheckUsersId,a.type,a.user_type as userType, a.jaso_user_id as jasoUserId,a.user_real_name as userRealName,a.about_id as aboutId, a.company_id as companyId,a.project_id as projectId,a.create_time as createTime, a.if_delete as ifDelete,b.user_icon as userIcon from quality_check_users a,jaso_user b where a.jaso_user_id=b.jaso_user_id and a.type=1 and a.if_delete=0
and a.company_id=#{companyId}
and a.project_id=#{projectId}

        select a.quality_check_users_id as qualityCheckUsersId,a.type,a.user_type as userType,
        a.jaso_user_id as jasoUserId,a.user_real_name as userRealName,a.about_id as aboutId,
        a.company_id as companyId,a.project_id as projectId,a.create_time as createTime,
        a.if_delete as ifDelete,b.user_icon as userIcon 
		from quality_check_users a,jaso_user b where a.if_delete=0 and a.jaso_user_id=b.jaso_user_id and a.type=1

           and a.company_id=#{companyId}


           and a.project_id=#{projectId}


           and a.about_id=#{qualityCheckId}
